  function handleInput(event) {
    if(event.target.id === "name"){
      setValue({...value, "name" :event.target.value});
    }else{
      setValue({...value, "year" :event.target.value});
    }
  }
  function handleSubmit(event) {
    event.preventDefault();
    console.log(value);
