      <form
        onSubmit={handleSubmit}
        className="h-[80px] w-3/4 flex items-center justify-center text-black"
      >
        <input
          onChange={handleInput}
          type="text"
          id="name"
          placeholder="Search..."
          className="w-full h-[40px] p-4 rounded-l-sm outline-none border-l-[.1px] border-t-[.1px] border-b-[.1px] border-black"
        />
        <input
          onChange={handleInput}
          type="text"
          placeholder="Search..."
          className="w-full h-[40px] p-4 rounded-l-sm outline-none border-l-[.1px] border-t-[.1px] border-b-[.1px] border-black"
        />
        <button
          type="submit"
          className="h-[40px] px-2 bg-white text-black border-[.1px] border-black rounded-r-sm"
        >
          Submit
        </button>
      </form>
      <div className="w-full flex-1 flex flex-col items-center border-[.5px] border-white">
        {data.map((item, id) => {
          return (
            <div
              key={id}
              className="w-3/4 h-[40px] flex items-center justify-between px-2 border-[.1px] border-black"
            >
              <p>{item.name}</p>
              <p>{item.year}</p>
              <p>{item.artist}</p>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
